document.addEventListener("DOMContentLoaded",function(){let e={cpf:e=>e.replace(/\D/g,"").replace(/(\d{3})(\d)/,"$1.$2").replace(/(\d{3})(\d)/,"$1.$2").replace(/(\d{3})(\d{1,2})$/,"$1-$2"),telefone:e=>e.replace(/\D/g,"").replace(/(\d{2})(\d)/,"($1) $2").replace(/(\d{5})(\d{4})$/,"$1-$2"),cep:e=>e.replace(/\D/g,"").replace(/(\d{5})(\d{3})$/,"$1-$2")};document.querySelectorAll("input[data-mask]").forEach(t=>{t.addEventListener("input",t=>{let a=t.target.getAttribute("data-mask");t.target.value=e[a](t.target.value)})})});
