document.addEventListener("DOMContentLoaded",()=>{let e=document.querySelectorAll("nav a"),t=document.querySelector("main");async function r(e){try{let r=await fetch(e),n=await r.text(),a=new DOMParser,o=a.parseFromString(n,"text/html"),l=o.querySelector("main").innerHTML;t.innerHTML=l,window.scrollTo(0,0)}catch(i){t.innerHTML="<p>Erro ao carregar conte\xfado.</p>",console.error("Erro no carregamento:",i)}}e.forEach(e=>{e.addEventListener("click",t=>{t.preventDefault();let n=e.getAttribute("href");n.endsWith(".html")&&(r(n),history.pushState({url:n},"",n))})}),window.addEventListener("popstate",e=>{e.state?.url&&r(e.state.url)})});
